from pydantic import BaseModel, Field
from rich.json import JSON


class AgentIO(BaseModel):

    def __str__(self):
        return self.model_dump_json()

    def __rich__(self):
        json_str = self.model_dump_json()
        return JSON(json_str)


class AgentInput(AgentIO):
    """This schema represents the input from the user to the AI agent."""

    input: str = Field(
        ...,
        description="The chat message sent by the user to the assistant.",
    )


class AgentOutput(AgentIO):
    """This schema represents the response generated by the chat agent."""

    output: str = Field(
        ...,
        description="The response from the assistant back to the user.",
    )
